import React, { Component } from "react";
import { throws } from "assert";

export class AddTask extends Component {
	constructor(props) {
		super(props);
		this.state = {
			name: "",
			description: "",
			date: new Date(),
			tasks: []
		};
	}

	onChangeName(e) {
		this.setState({
			name: e.target.value
		});
	}
	render() {
		return (
			<div>
				<p>you are on the add task page</p>
			</div>
		);
	}
}

export default AddTask;
