import React, { Component } from "react";
import axios from "axios";

export class TaskList extends Component {
	constructor(props) {
		this.state = {
			tasks: []
		};

		this.deletTask = this.deleteTask
	}

	componentDidMount() {
		axios.get("http://localhost:5000/task")
			.then(response => {
				if (response.data.length > 0){
					this.setState({
						tasks: response.data.map()
					})
				}
			})
	}
	render() {
		return (
			<div>
				<p>you are on the task list page</p>
			</div>
		);
	}
}

export default TaskList;
