import React, { Component } from "react";
import { throws } from "assert";

export class AddTask extends Component {
	constructor(props) {
		super(props);
		this.state = {
			name: "",
			description: "",
			date: new Date(),
			tasks: []
		};

		this.onChangeName = this.onChangeName.bind(this)
		this.onChangeDescription = this.on
		this.onChangeDate
	}

	onChangeName(e) {
		this.setState({
			name: e.target.value
		});
	}
	onChangeDescription(e) {
		this.setState({
			description: e.target.value
		});
	}
	onChangeDate(date) {
		this.setState({
			date: date
		});
	}

	onSubmit(e) {
		e.preventDefault();

		const task = {
			name: this.state.name,
			description: this.state.description,
			date: this.state.date
		};

		console.log(task);

		window.location = "/";
	}

	render() {
		return (
			<div>
				<p>you are on the add task page</p>
			</div>
		);
	}
}

export default AddTask;
